import React, { useState, useEffect } from 'react';
import {
  Box,
  Container,
  Paper,
  Typography,
  TextField,
  Button,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  Alert,
  Snackbar,
} from '@mui/material';
import SaveIcon from '@mui/icons-material/Save';
import Sidebar from '../components/Sidebar';

const Grades = () => {
  const [monhocs, setMonHocs] = useState([]);  
  const [diems, setDiems] = useState([]);
  const [students, setStudents] = useState([]);
  const [selectedSubject, setSelectedSubject] = useState(''); // Để lưu môn học được chọn
  const [filteredDiems, setFilteredDiems] = useState([]);
  const [openSnackbar, setOpenSnackbar] = useState(false);



  useEffect(() => {
    if (selectedSubject) {
      const filteredData = diems.filter(diem => diem.MaMH === selectedSubject);
      setFilteredDiems(filteredData);
    } else {
      setFilteredDiems(diems); // Nếu không chọn môn học, hiển thị tất cả
    }
  }, [selectedSubject, diems]);

  const handleGradeChange = async (studentId, field, value) => {
    const numValue = value === '' ? '' : Number(value);
    console.log(value)
  
    if (value === '' || (numValue >= 0 && numValue <= 10)) {
      // Cập nhật điểm trong state
      const updatedStudents = students.map((student) => {
        if (student.MaSV === studentId) {
          const updatedStudent = { ...student, [field]: value };
          // Tính điểm tổng kết nếu không phải trường 'total'
          if (field !== 'total') {
            const attendance = updatedStudent.DiemCC === '' ? 0 : Number(updatedStudent.DiemCC);
            const midterm = updatedStudent.DiemGK === '' ? 0 : Number(updatedStudent.DiemGK);
            const final = updatedStudent.DiemCK === '' ? 0 : Number(updatedStudent.DiemCK);
            updatedStudent.DiemTong = ((attendance * 0.1) + (midterm * 0.3) + (final * 0.6)).toFixed(1);
           
          }
          
  
          return updatedStudent;
        }
        return student;
      });

      // Cập nhật lại state
      setStudents(updatedStudents);
      // Gửi request cập nhật điểm
      try {
        const response = await fetch(`http://localhost:3000/diem/${studentId}`, {
          method: 'PUT',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify(updatedStudents.find(student => student.MaSV === studentId)),  // Gửi sinh viên đã cập nhật điểm
        });
  
        if (response.ok) {
          console.log('✅ Điểm đã được cập nhật');
        } else {
          console.error('❌ Lỗi khi cập nhật điểm từ server');
        }
      } catch (error) {
        console.error('❌ Lỗi kết nối đến API:', error);
      }
    } else {
      console.error('❌ Điểm không hợp lệ');
    }
  };
  
  
  const handleSave = () => {
    setOpenSnackbar(true);
    // Thêm logic lưu điểm vào đây
  };

  useEffect(() => {
    

    const fetchMonHoc = async () => {
      try {
        const response = await fetch('http://localhost:3000/monhoc');
        const data = await response.json();
        setMonHocs(data);
      } catch (error) {
        console.error('Lỗi khi tải dữ liệu môn học:', error);
      }
    };

    const fetchDiem = async () => {
      try {
        const response = await fetch('http://localhost:3000/diem');
        const data = await response.json();
        setDiems(data);
      } catch (error) {
        console.error('Lỗi khi tải dữ liệu điểm:', error);
      }
    };

    fetchDiem();
    fetchMonHoc();
    
  }, []);

  const handleSubjectChange = (event) => {
    setSelectedSubject(event.target.value);
  };

  return (
    <Box sx={{ display: 'flex' }}>
      <Sidebar />
      <Box
        component="main"
        sx={{
          flexGrow: 1,
          p: 3,
          ml: '240px',
          bgcolor: '#f5f5f5',
          minHeight: '100vh',
        }}
      >
        <Container maxWidth="lg">
          <Box sx={{ mb: 4 }}>
            <Typography variant="h4" sx={{ fontWeight: 600, color: '#1a2f4d' }}>
              Nhập điểm
            </Typography>
          </Box>

          <Paper sx={{ p: 3, mb: 3 }}>
            <Box sx={{ display: 'flex', gap: 2, mb: 3 }}>
              

              <FormControl fullWidth>
                <InputLabel>Môn học</InputLabel>
                <Select
                  value={selectedSubject}
                  onChange={handleSubjectChange} // Thêm sự kiện thay đổi môn học
                  defaultValue=""
                >
                  {Array.isArray(monhocs) && monhocs.length > 0 ? (
                    monhocs.map((monhoc) => (
                      <MenuItem key={monhoc.MaMH} value={monhoc.MaMH}>
                        {monhoc.TenMH}
                      </MenuItem>
                    ))
                  ) : (
                    <MenuItem disabled>Không có môn học nào</MenuItem>
                  )}
                </Select>
              </FormControl>
            </Box>

            {selectedSubject ? ( // Kiểm tra xem có môn học được chọn không
              <>
                <TableContainer>
                  <Table>
                    <TableHead>
                      <TableRow>
                        <TableCell>Mã Sinh Viên</TableCell>
                        <TableCell>Mã Môn Học</TableCell>
                        <TableCell align="center">Điểm chuyên cần (10%)</TableCell>
                        <TableCell align="center">Điểm giữa kỳ (30%)</TableCell>
                        <TableCell align="center">Điểm cuối kỳ (60%)</TableCell>
                        <TableCell align="center">Tổng kết</TableCell>
                      </TableRow>
                    </TableHead>
                    <TableBody>
                      {filteredDiems.map((diem) => (
                        <TableRow key={diem.id}>
                          <TableCell>{diem.MaSV}</TableCell>
                          <TableCell>{diem.MaMH}</TableCell>
                          <TableCell align="center">
                            <TextField
                              type="number"
                              value={diem.DiemCC}
                              onChange={(e) => handleGradeChange(diem.MaSV, 'attendance', e.target.value)}
                              inputProps={{ min: 0, max: 10, step: 0.1 }}
                              sx={{ width: '100px' }}
                            />
                          </TableCell>
                          <TableCell align="center">
                            <TextField
                              type="number"
                              value={diem.DiemGK}
                              onChange={(e) => handleGradeChange(diem.MaSV, 'midterm', e.target.value)}
                              inputProps={{ min: 0, max: 10, step: 0.1 }}
                              sx={{ width: '100px' }}
                            />
                          </TableCell>
                          <TableCell align="center">
                            <TextField
                              type="number"
                              value={diem.DiemCK}
                              onChange={(e) => handleGradeChange(diem.MaSV, 'final', e.target.value)}
                              inputProps={{ min: 0, max: 10, step: 0.1 }}
                              sx={{ width: '100px' }}
                            />
                          </TableCell>
                          <TableCell align="center">
                            <Typography
                              sx={{
                                fontWeight: 600,
                                color: diem.DiemTong >= 4 ? 'success.main' : 'error.main',
                              }}
                            >
                              {diem.DiemTong}
                            </Typography>
                          </TableCell>
                        </TableRow>
                      ))}
                    </TableBody>
                  </Table>
                </TableContainer>

                <Box sx={{ mt: 3, display: 'flex', justifyContent: 'flex-end' }}>
                  <Button
                    variant="contained"
                    startIcon={<SaveIcon />}
                    onClick={handleSave}
                    sx={{
                      bgcolor: '#2196F3',
                      '&:hover': { bgcolor: '#1976D2' },
                    }}
                  >
                    Lưu điểm
                  </Button>
                </Box>
              </>
            ) : (
              <Alert severity="info">
                Vui lòng chọn môn học để nhập điểm
              </Alert>
            )}
          </Paper>
        </Container>

        <Snackbar
          open={openSnackbar}
          autoHideDuration={3000}
          onClose={() => setOpenSnackbar(false)}
          anchorOrigin={{ vertical: 'bottom', horizontal: 'right' }}
        >
          <Alert onClose={() => setOpenSnackbar(false)} severity="success">
            Đã lưu điểm thành công!
          </Alert>
        </Snackbar>
      </Box>
    </Box>
  );
};

export default Grades;
