import React, { useState, useEffect } from 'react';
import {
  Table, TableBody, TableCell, TableContainer, TableHead, TableRow,
  Paper, TextField, MenuItem, Typography, Box
} from '@mui/material';

const DiemForm = () => {
  const [diems, setDiems] = useState([]);
  const [subjects, setSubjects] = useState([]);
  const [selectedSubject, setSelectedSubject] = useState('');
  const [filteredDiems, setFilteredDiems] = useState([]);

  // Lấy tất cả điểm và môn học
  useEffect(() => {
    fetch('http://localhost:3000/diem')
      .then((res) => res.json())
      .then((data) => {
        setDiems(data);
      });

    fetch('http://localhost:3000/monhoc')
      .then((res) => res.json())
      .then((data) => setSubjects(data));
  }, []);

  // Lọc điểm theo môn học
  useEffect(() => {
    if (selectedSubject) {
      const filtered = diems.filter((d) => d.MaMH === selectedSubject);
      setFilteredDiems(filtered);
    } else {
      setFilteredDiems([]);
    }
  }, [selectedSubject, diems]);

  const handleGradeChange = async (studentId, field, value) => {
    const numValue = value === '' ? '' : Number(value);

    if (value === '' || (numValue >= 0 && numValue <= 10)) {
      const updatedDiems = diems.map(diem => {
        if (diem.MaSV === studentId && diem.MaMH === selectedSubject) {
          const updatedDiem = { ...diem };

          if (field === 'attendance') updatedDiem.DiemCC = numValue;
          if (field === 'midterm') updatedDiem.DiemGK = numValue;
          if (field === 'final') updatedDiem.DiemCK = numValue;

          const cc = updatedDiem.DiemCC || 0;
          const gk = updatedDiem.DiemGK || 0;
          const ck = updatedDiem.DiemCK || 0;

          updatedDiem.DiemTong = ((cc * 0.1) + (gk * 0.3) + (ck * 0.6)).toFixed(1);

          return updatedDiem;
        }
        return diem;
      });

      setDiems(updatedDiems);

      const target = updatedDiems.find(
        d => d.MaSV === studentId && d.MaMH === selectedSubject
      );

      try {
        const response = await fetch(`http://localhost:3000/diem/${studentId}`, {
          method: 'PUT',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({
            MaSV: target.MaSV,
            MaMH: target.MaMH,
            DiemCC: target.DiemCC,
            DiemGK: target.DiemGK,
            DiemCK: target.DiemCK,
            DiemTong: target.DiemTong,
          }),
        });

        if (!response.ok) throw new Error('Cập nhật điểm thất bại');
        console.log('✅ Cập nhật điểm thành công');
      } catch (err) {
        console.error('❌ Lỗi khi gửi dữ liệu:', err);
      }
    }
  };

  return (
    <Box p={3}>
      <Typography variant="h5" gutterBottom>Nhập điểm sinh viên</Typography>

      <TextField
        select
        label="Chọn môn học"
        value={selectedSubject}
        onChange={(e) => setSelectedSubject(e.target.value)}
        fullWidth
        margin="normal"
      >
        {subjects.map((subject) => (
          <MenuItem key={subject.MaMH} value={subject.MaMH}>
            {subject.TenMH}
          </MenuItem>
        ))}
      </TextField>

      {filteredDiems.length > 0 && (
        <TableContainer component={Paper} sx={{ mt: 3 }}>
          <Table>
            <TableHead>
              <TableRow>
                <TableCell>Mã sinh viên</TableCell>
                <TableCell align="center">Điểm CC</TableCell>
                <TableCell align="center">Điểm GK</TableCell>
                <TableCell align="center">Điểm CK</TableCell>
                <TableCell align="center">Tổng kết</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {filteredDiems.map((diem) => (
                <TableRow key={diem.MaSV}>
                  <TableCell>{diem.MaSV}</TableCell>
                  <TableCell align="center">
                    <TextField
                      type="number"
                      value={diem.DiemCC ?? ''}
                      onChange={(e) =>
                        handleGradeChange(diem.MaSV, 'attendance', e.target.value)
                      }
                      inputProps={{ min: 0, max: 10 }}
                      sx={{ width: 80 }}
                    />
                  </TableCell>
                  <TableCell align="center">
                    <TextField
                      type="number"
                      value={diem.DiemGK ?? ''}
                      onChange={(e) =>
                        handleGradeChange(diem.MaSV, 'midterm', e.target.value)
                      }
                      inputProps={{ min: 0, max: 10 }}
                      sx={{ width: 80 }}
                    />
                  </TableCell>
                  <TableCell align="center">
                    <TextField
                      type="number"
                      value={diem.DiemCK ?? ''}
                      onChange={(e) =>
                        handleGradeChange(diem.MaSV, 'final', e.target.value)
                      }
                      inputProps={{ min: 0, max: 10 }}
                      sx={{ width: 80 }}
                    />
                  </TableCell>
                  <TableCell align="center">{diem.DiemTong}</TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </TableContainer>
      )}
    </Box>
  );
};

export default DiemForm;
