import React, { useState, useEffect } from 'react';
import {
  Box,
  Container,
  Grid,
  Paper,
  Typography,
  TextField,
  Button,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  IconButton,
} from '@mui/material';
import EditIcon from '@mui/icons-material/Edit';
import DeleteIcon from '@mui/icons-material/Delete';
import AddIcon from '@mui/icons-material/Add';
import Sidebar from '../components/Sidebar';

const Settings = () => {
  const [gradeSettings, setGradeSettings] = useState({
    minGrade: '4',
    maxGrade: '10',
    excellentGrade: '8.5',
    goodGrade: '7',
    averageGrade: '5.5',
    belowAverageGrade: '4',
    failGrade: '0',
  });

  const [monhocs, setMonHocs] = useState([]);

  const handleGradeSettingsChange = (field) => (event) => {
    setGradeSettings({
      ...gradeSettings,
      [field]: event.target.value,
    });
  };

  useEffect(() => {
    const fetchMonHoc = async () => {
      try {
        const response = await fetch('http://localhost:3000/monhoc');
        const data = await response.json();
        setMonHocs(data);
      } catch (error) {
        console.error('Lỗi khi tải dữ liệu lớp:', error);
      }
    };
    fetchMonHoc();
  }, []);

  const handleDeleteMonHoc = async (MaMH) => {
    try {
      // Make an API call to delete the subject
      const response = await fetch(`http://localhost:3000/monhoc/${MaMH}`, {
        method: 'DELETE',
      });

      if (!response.ok) {
        throw new Error('Lỗi khi xóa môn học');
      }

      // Update the state to remove the deleted subject from the list
      setMonHocs(monhocs.filter((monhoc) => monhoc.MaMH !== MaMH));
    } catch (error) {
      console.error('Lỗi xóa môn học:', error);
    }
  };

  return (
    <Box sx={{ display: 'flex' }}>
      <Sidebar />
      <Box
        component="main"
        sx={{
          flexGrow: 1,
          p: 3,
          ml: '240px',
          bgcolor: '#f5f5f5',
          minHeight: '100vh',
        }}
      >
        <Container maxWidth="lg">
          <Typography variant="h4" sx={{ mb: 4 }}>
            Cài đặt hệ thống
          </Typography>

          <Paper sx={{ p: 3, mb: 3 }}>
            <Typography variant="h6" gutterBottom>
              Cài đặt điểm
            </Typography>
            <Grid container spacing={3}>
              <Grid item xs={12} md={4}>
                <TextField
                  fullWidth
                  label="Điểm đạt tối thiểu"
                  value={gradeSettings.minGrade}
                  onChange={handleGradeSettingsChange('minGrade')}
                  type="number"
                  inputProps={{ step: '0.1' }}
                />
              </Grid>
              <Grid item xs={12} md={4}>
                <TextField
                  fullWidth
                  label="Điểm xuất sắc"
                  value={gradeSettings.excellentGrade}
                  onChange={handleGradeSettingsChange('excellentGrade')}
                  type="number"
                  inputProps={{ step: '0.1' }}
                />
              </Grid>
              <Grid item xs={12} md={4}>
                <TextField
                  fullWidth
                  label="Điểm giỏi"
                  value={gradeSettings.goodGrade}
                  onChange={handleGradeSettingsChange('goodGrade')}
                  type="number"
                  inputProps={{ step: '0.1' }}
                />
              </Grid>
              <Grid item xs={12} md={4}>
                <TextField
                  fullWidth
                  label="Điểm khá"
                  value={gradeSettings.averageGrade}
                  onChange={handleGradeSettingsChange('averageGrade')}
                  type="number"
                  inputProps={{ step: '0.1' }}
                />
              </Grid>
              <Grid item xs={12} md={4}>
                <TextField
                  fullWidth
                  label="Điểm trung bình"
                  value={gradeSettings.belowAverageGrade}
                  onChange={handleGradeSettingsChange('belowAverageGrade')}
                  type="number"
                  inputProps={{ step: '0.1' }}
                />
              </Grid>
              <Grid item xs={12} md={4}>
                <TextField
                  fullWidth
                  label="Điểm yếu"
                  value={gradeSettings.failGrade}
                  onChange={handleGradeSettingsChange('failGrade')}
                  type="number"
                  inputProps={{ step: '0.1' }}
                />
              </Grid>
            </Grid>
            <Box sx={{ mt: 2, display: 'flex', justifyContent: 'flex-end' }}>
              <Button variant="contained" color="primary">
                Lưu cài đặt điểm
              </Button>
            </Box>
          </Paper>

          <Paper sx={{ p: 3 }}>
            <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 3 }}>
              <Typography variant="h6">Quản lý môn học</Typography>
              <Button
                variant="contained"
                color="primary"
                startIcon={<AddIcon />}
              >
                Thêm môn học
              </Button>
            </Box>

            <TableContainer>
              <Table>
                <TableHead>
                  <TableRow>
                    <TableCell>Mã môn học</TableCell>
                    <TableCell>Tên môn học</TableCell>
                    <TableCell align="center">Số tín chỉ</TableCell>
                    <TableCell align="center">Hệ số Chuyên Cần</TableCell>
                    <TableCell align="center">Hệ số Giữa kỳ</TableCell>
                    <TableCell align="center">Hệ số Cuối kỳ</TableCell>
                    <TableCell align="center">Thao tác</TableCell>
                  </TableRow>
                </TableHead>
                <TableBody>
                  {monhocs.map((monhoc) => (
                    <TableRow key={monhoc.MaMH}>
                      <TableCell>{monhoc.MaMH}</TableCell>
                      <TableCell>{monhoc.TenMH}</TableCell>
                      <TableCell align="center">{monhoc.SoTinChi}</TableCell>
                      <TableCell align="center">{monhoc.HeSoCC}</TableCell>
                      <TableCell align="center">{monhoc.HeSoGK}</TableCell>
                      <TableCell align="center">{monhoc.HeSoCK}</TableCell>
                      <TableCell align="center">
                        <IconButton
                          color="error"
                          size="small"
                          onClick={() => handleDeleteMonHoc(monhoc.MaMH)}
                        >
                          <DeleteIcon />
                        </IconButton>
                      </TableCell>
                    </TableRow>
                  ))}
                </TableBody>
              </Table>
            </TableContainer>
          </Paper>
        </Container>
      </Box>
    </Box>
  );
};

export default Settings;
