import React, { useState, useEffect } from 'react';
import {
  Box,
  Container,
  Paper,
  Typography,
  TextField,
  Button,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  Alert,
  Snackbar,
} from '@mui/material';
import SaveIcon from '@mui/icons-material/Save';
import Sidebar from '../components/Sidebar';

const Grades = () => {
  const [selectedSubject, setSelectedSubject] = useState('');
  const [lops, setLops] = useState([]);  // Khởi tạo lops là mảng rỗng

const fetchLops = async () => {
  try {
    const response = await fetch('http://localhost:3000/lop');
    if (!response.ok) {
      throw new Error(`HTTP error! status: ${response.status}`);
    }
    const data = await response.json();
    console.log(data);  // Kiểm tra xem dữ liệu trả về có phải là mảng không
    setLops(Array.isArray(data) ? data : []); // Đảm bảo data là mảng trước khi gán vào state
  } catch (error) {
    console.error('Lỗi khi tải dữ liệu lớp:', error);
  }
};

  const [students, setStudents] = useState([
    { id: '2021001', name: 'Nguyễn Văn A', midterm: '', final: '', attendance: '', total: '' },
    { id: '2021002', name: 'Trần Thị B', midterm: '', final: '', attendance: '', total: '' },
  ]);
  const [openSnackbar, setOpenSnackbar] = useState(false);



  const subjects = [
    { id: 'NMLT', name: 'Nhập môn lập trình' },
    { id: 'KTLT', name: 'Kỹ thuật lập trình' },
    { id: 'CTDL', name: 'Cấu trúc dữ liệu và giải thuật' },
    { id: 'LTHDT', name: 'Lập trình hướng đối tượng' },
    { id: 'CSDL', name: 'Cơ sở dữ liệu' },
    { id: 'MMT', name: 'Mạng máy tính' },
    { id: 'HDH', name: 'Hệ điều hành' },
    { id: 'CNPM', name: 'Công nghệ phần mềm' },
    { id: 'TTNT', name: 'Trí tuệ nhân tạo' },
    { id: 'LTW', name: 'Lập trình Web' },
    { id: 'JAVA', name: 'Lập trình Java' },
    { id: 'ATTT', name: 'An toàn thông tin' },
    { id: 'DTDM', name: 'Điện toán đám mây' },
    { id: 'LTDD', name: 'Lập trình di động' },
    { id: 'TRR', name: 'Toán rời rạc' },
    { id: 'XSTK', name: 'Xác suất thống kê' },
  ];

  const handleGradeChange = (studentId, field, value) => {
    const numValue = value === '' ? '' : Number(value);
    if (value === '' || (numValue >= 0 && numValue <= 10)) {
      setStudents(students.map(student => {
        if (student.id === studentId) {
          const updatedStudent = { ...student, [field]: value };
          // Tính điểm tổng kết
          if (field !== 'total') {
            const attendance = updatedStudent.attendance === '' ? 0 : Number(updatedStudent.attendance);
            const midterm = updatedStudent.midterm === '' ? 0 : Number(updatedStudent.midterm);
            const final = updatedStudent.final === '' ? 0 : Number(updatedStudent.final);
            updatedStudent.total = ((attendance * 0.1) + (midterm * 0.3) + (final * 0.6)).toFixed(1);
          }
          return updatedStudent;
        }
        return student;
      }));
    }
  };

  const handleSave = () => {
    setOpenSnackbar(true);
    // Thêm logic lưu điểm vào đây
  };

  useEffect(() => {
    const fetchLops = async () => {
      try {
        const response = await fetch('http://localhost:3000/lop');
        const data = await response.json();
        setLops(data);
      } catch (error) {
        console.error('Lỗi khi tải dữ liệu lớp:', error);
      }
    };
  
    fetchLops();
  }, []);
  return (
    <Box sx={{ display: 'flex' }}>
      <Sidebar />
      <Box
        component="main"
        sx={{
          flexGrow: 1,
          p: 3,
          ml: '240px',
          bgcolor: '#f5f5f5',
          minHeight: '100vh',
        }}
      >
        <Container maxWidth="lg">
          <Box sx={{ mb: 4 }}>
            <Typography variant="h4" sx={{ fontWeight: 600, color: '#1a2f4d' }}>
              Nhập điểm
            </Typography>
          </Box>

          <Paper sx={{ p: 3, mb: 3 }}>
            <Box sx={{ display: 'flex', gap: 2, mb: 3 }}>
            <FormControl fullWidth>
            <InputLabel>Lớp</InputLabel>
            <Select label="Lớp">
              {Array.isArray(lops) && lops.length > 0 ? (
                lops.map((cls) => (
                  <MenuItem key={cls.MaLop} value={cls.MaLop}>
                    {cls.TenLop}
                  </MenuItem>
                ))
              ) : (
                <MenuItem disabled>Không có lớp nào</MenuItem>
              )}
            </Select>
          </FormControl>


              <FormControl fullWidth>
                <InputLabel>Môn học</InputLabel>
                <Select
                  value={selectedSubject}
                  label="Môn học"
                  onChange={(e) => setSelectedSubject(e.target.value)}
                >
                  {subjects.map((subject) => (
                    <MenuItem key={subject.id} value={subject.id}>
                      {subject.name}
                    </MenuItem>
                  ))}
                </Select>
              </FormControl>
            </Box>

            {lops && selectedSubject ? (
              <>
                <TableContainer>
                  <Table>
                    <TableHead>
                      <TableRow>
                        <TableCell>Mã SV</TableCell>
                        <TableCell>Họ và tên</TableCell>
                        <TableCell align="center">Điểm chuyên cần (10%)</TableCell>
                        <TableCell align="center">Điểm giữa kỳ (30%)</TableCell>
                        <TableCell align="center">Điểm cuối kỳ (60%)</TableCell>
                        <TableCell align="center">Tổng kết</TableCell>
                      </TableRow>
                    </TableHead>
                    <TableBody>
                      {students.map((student) => (
                        <TableRow key={student.id}>
                          <TableCell>{student.id}</TableCell>
                          <TableCell>{student.name}</TableCell>
                          <TableCell align="center">
                            <TextField
                              type="number"
                              value={student.attendance}
                              onChange={(e) => handleGradeChange(student.id, 'attendance', e.target.value)}
                              inputProps={{ min: 0, max: 10, step: 0.1 }}
                              sx={{ width: '100px' }}
                            />
                          </TableCell>
                          <TableCell align="center">
                            <TextField
                              type="number"
                              value={student.midterm}
                              onChange={(e) => handleGradeChange(student.id, 'midterm', e.target.value)}
                              inputProps={{ min: 0, max: 10, step: 0.1 }}
                              sx={{ width: '100px' }}
                            />
                          </TableCell>
                          <TableCell align="center">
                            <TextField
                              type="number"
                              value={student.final}
                              onChange={(e) => handleGradeChange(student.id, 'final', e.target.value)}
                              inputProps={{ min: 0, max: 10, step: 0.1 }}
                              sx={{ width: '100px' }}
                            />
                          </TableCell>
                          <TableCell align="center">
                            <Typography
                              sx={{
                                fontWeight: 600,
                                color: student.total >= 4 ? 'success.main' : 'error.main',
                              }}
                            >
                              {student.total}
                            </Typography>
                          </TableCell>
                        </TableRow>
                      ))}
                    </TableBody>
                  </Table>
                </TableContainer>

                <Box sx={{ mt: 3, display: 'flex', justifyContent: 'flex-end' }}>
                  <Button
                    variant="contained"
                    startIcon={<SaveIcon />}
                    onClick={handleSave}
                    sx={{
                      bgcolor: '#2196F3',
                      '&:hover': { bgcolor: '#1976D2' },
                    }}
                  >
                    Lưu điểm
                  </Button>
                </Box>
              </>
            ) : (
              <Alert severity="info">
                Vui lòng chọn lớp và môn học để nhập điểm
              </Alert>
            )}
          </Paper>
        </Container>

        <Snackbar
          open={openSnackbar}
          autoHideDuration={3000}
          onClose={() => setOpenSnackbar(false)}
          anchorOrigin={{ vertical: 'bottom', horizontal: 'right' }}
        >
          <Alert onClose={() => setOpenSnackbar(false)} severity="success">
            Đã lưu điểm thành công!
          </Alert>
        </Snackbar>
      </Box>
    </Box>
  );
};

export default Grades;
