import React, { useState, useEffect } from 'react';
import {
  Box,
  Container,
  Grid,
  Paper,
  Typography,
  Button,
} from '@mui/material';
import PeopleIcon from '@mui/icons-material/People';
import MenuBookIcon from '@mui/icons-material/MenuBook';
import GradeIcon from '@mui/icons-material/Grade';
import SettingsIcon from '@mui/icons-material/Settings';
import Sidebar from '../components/Sidebar';

const StatCard = ({ icon, value, label }) => (
  <Paper
    elevation={2}
    sx={{
      p: 3,
      display: 'flex',
      flexDirection: 'column',
      alignItems: 'center',
      bgcolor: 'white',
      borderRadius: 2,
    }}
  >
    <Box sx={{ color: '#1976d2', mb: 1 }}>{icon}</Box>
    <Typography variant="h4" component="div" gutterBottom>
      {value}
    </Typography>
    <Typography color="text.secondary">{label}</Typography>
  </Paper>
);

const ActivityItem = ({ title, description, time }) => (
  <Box sx={{ py: 2, borderBottom: '1px solid #eee' }}>
    <Typography variant="subtitle1" gutterBottom>
      {title}
    </Typography>
    <Typography variant="body2" color="text.secondary">
      {description}
    </Typography>
    <Typography variant="caption" color="text.secondary">
      {time}
    </Typography>
  </Box>
);

const Dashboard = () => {
  const [totalStudents, setTotalStudents] = useState(0);
  const [subjectsCount, setSubjectsCount] = useState(0);
  // Fetch the total number of students from the API
  useEffect(() => {
    const fetchStudents = async () => {
      try {
        const response = await fetch('http://localhost:3000/sinhvien'); // Replace with your actual API URL
        const data = await response.json();
        setTotalStudents(data.length); // Assuming data is an array of students
      } catch (error) {
        console.error('Error fetching student data:', error);
      }
    };
    const fetchMonHoc = async () => {
      try {
        const response = await fetch('http://localhost:3000/MonHoc'); // Replace with your actual API URL
        const data = await response.json();
        setSubjectsCount(data.length); // Assuming data is an array of students
      } catch (error) {
        console.error('Error fetching student data:', error);
      }
    };
    fetchMonHoc();
    fetchStudents();
  }, []); // Empty dependency array ensures it only runs once

  return (
    <Box sx={{ display: 'flex' }}>
      <Sidebar />
      <Box
        component="main"
        sx={{
          flexGrow: 1,
          p: 3,
          ml: '240px',
          bgcolor: '#f5f5f5',
          minHeight: '100vh',
        }}
      >
        <Container maxWidth="lg">
          <Typography variant="h4" sx={{ mb: 4 }}>
            Tổng quan
          </Typography>
          
          <Grid container spacing={3}>
            <Grid item xs={12} sm={6} md={2}>
              <StatCard
                icon={<PeopleIcon sx={{ fontSize: 40 }} />}
                value={totalStudents} // Dynamically display the number of students
                label="Tổng số sinh viên"
              />
            </Grid>
            <Grid item xs={12} sm={6} md={2}>
              <StatCard
                icon={<MenuBookIcon sx={{ fontSize: 40 }} />}
                value={subjectsCount}
                label="Môn học"
              />
            </Grid>
          </Grid>

          <Grid container spacing={3} sx={{ mt: 2 }}>
            <Grid item xs={12} md={8}>
              <Paper sx={{ p: 3 }}>
                <Typography variant="h6" gutterBottom>
                  Hoạt động gần đây
                </Typography>
                <ActivityItem
                  title="Cập nhật điểm Toán cao cấp"
                  description="Đã cập nhật điểm cho 30 sinh viên"
                  time="3 phút trước"
                />
                <ActivityItem
                  title="Thêm môn học mới"
                  description="Đã thêm môn Vật lý đại cương"
                  time="2 giờ trước"
                />
                <ActivityItem
                  title="Xuất báo cáo điểm"
                  description="Đã xuất báo cáo điểm học kỳ 1"
                  time="1 ngày trước"
                />
              </Paper>
            </Grid>
            <Grid item xs={12} md={4}>
              <Paper sx={{ p: 3 }}>
                <Typography variant="h6" gutterBottom>
                  Thao tác nhanh
                </Typography>
                <Grid container spacing={2}>
                  <Grid item xs={12}>
                    <Button
                      variant="contained"
                      fullWidth
                      startIcon={<GradeIcon />}
                      sx={{ mb: 2 }}
                    >
                      Nhập điểm mới
                    </Button>
                  </Grid>
                  <Grid item xs={12}>
                    <Button
                      variant="contained"
                      color="success"
                      fullWidth
                      startIcon={<PeopleIcon />}
                      sx={{ mb: 2 }}
                    >
                      Thêm sinh viên
                    </Button>
                  </Grid>
                  <Grid item xs={12}>
                    <Button
                      variant="contained"
                      color="info"
                      fullWidth
                      startIcon={<MenuBookIcon />}
                      sx={{ mb: 2 }}
                    >
                      Xuất báo cáo
                    </Button>
                  </Grid>
                  <Grid item xs={12}>
                    <Button
                      variant="contained"
                      color="inherit"
                      fullWidth
                      startIcon={<SettingsIcon />}
                    >
                      Cài đặt hệ thống
                    </Button>
                  </Grid>
                </Grid>
              </Paper>
            </Grid>
          </Grid>
        </Container>
      </Box>
    </Box>
  );
};

export default Dashboard;
