import React, { useState, useEffect } from 'react';
import {
  Box,
  Container,
  Paper,
  Typography,
  TextField,
  Button,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  IconButton,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  InputAdornment,
} from '@mui/material';
import {
  Add as AddIcon,
  Edit as EditIcon,
  Delete as DeleteIcon,
  Search as SearchIcon,
} from '@mui/icons-material';
import Sidebar from '../components/Sidebar';

const Students = () => {
  const [students, setStudents] = useState([]);
  const [lops, setLops] = useState([]);
  const [selectedStudent, setSelectedStudent] = useState(null);
  const [openDialog, setOpenDialog] = useState(false);
  const [searchTerm, setSearchTerm] = useState('');
  const [formData, setFormData] = useState({
    id: '',
    name: '',
    class: '',
    gender: '',
    email: '',
    phone: '',
  });

  const handleOpenDialog = (student = null) => {
    if (student) {
      setFormData({
        id: student.MaSV,
        name: student.HoTen,
        class: student.MaLop,
        gender: student.GioiTinh,
        email: student.Email,
        phone: student.SoDT,
      });
      setSelectedStudent(student);
    } else {
      setFormData({
        id: '',
        name: '',
        class: '',
        gender: '',
        email: '',
        phone: '',
      });
      setSelectedStudent(null);
    }
    setOpenDialog(true);
  };

  const handleCloseDialog = () => {
    setOpenDialog(false);
    setSelectedStudent(null);
  };


  

  const filteredStudents = students.filter((student) =>
    Object.values(student).some((value) =>
      value.toLowerCase().includes(searchTerm.toLowerCase())
    )
  );

  useEffect(() => {
    const fetchSinhvien = async () => {
      try {
        const response = await fetch('http://localhost:3000/sinhvien');
        const data = await response.json();
        setStudents(data);
      } catch (error) {
        console.error('Lỗi khi tải dữ liệu sinh viên:', error);
      }
    };
    const fetchLops = async () => {
      try {
        const response = await fetch('http://localhost:3000/lop');
        const data = await response.json();
        setLops(data);
      } catch (error) {
        console.error('Lỗi khi tải dữ liệu lớp:', error);
      }
    };
    fetchLops();
    fetchSinhvien();
  }, []);

  const convertToVietnamDate = (utcDate) => {
    const date = new Date(utcDate);
    const vnDate = new Date(date.getTime() + 7 * 60 * 60 * 1000);
    return vnDate.toLocaleDateString();
  };

  //AddOrUpdate 
  const handleSubmit = async () => {
    const newStudent = {
      MaSV: formData.id,
      HoTen: formData.name,
      MaLop: formData.class,
      GioiTinh: formData.gender,
      Email: formData.email,
      SoDT: formData.phone,
    };
  
    try {
      if (selectedStudent) {
        // Cập nhật sinh viên (PUT)
        const response = await fetch(`http://localhost:3000/sinhvien/${formData.id}`, {
          method: 'PUT',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify(newStudent),
        });
  
        if (!response.ok) throw new Error('Cập nhật thất bại');
  
        // Cập nhật lại danh sách trong state
        setStudents((prev) =>
          prev.map((s) => (s.MaSV === selectedStudent.MaSV ? newStudent : s))
        );
      } else {
        // Thêm sinh viên mới (POST)
        const response = await fetch('http://localhost:3000/sinhvien', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify(newStudent),
        });
  
        if (!response.ok) throw new Error('Thêm thất bại');
  
        const created = await response.json();
  
        // Thêm vào danh sách hiện tại
        setStudents((prev) => [...prev, created]);
      }
  
      handleCloseDialog();
    } catch (error) {
      console.error('Lỗi khi lưu sinh viên:', error);
      alert('Có lỗi xảy ra khi lưu thông tin sinh viên!');
    }
  };
  
  const handleDelete = (MaSV) => {
    // Sending DELETE request to API
    fetch(`http://localhost:3000/sinhvien/${MaSV}`, {
        method: 'DELETE',
    })
    .then(response => response.json())
    .then(data => {
        if (data.message === 'Xóa sinh viên thành công') {
            // Remove the deleted student from the state
            setStudents((prevStudents) =>
                prevStudents.filter(student => student.MaSV !== MaSV)
            );
            alert('Sinh viên đã được xóa');
        } else {
            alert(data.message); // If there's an issue, show the message
        }
    })
    .catch((error) => {
        console.error('Error deleting student:', error);
        alert('Có lỗi xảy ra khi xóa sinh viên.');
    });
};
  return (
    <Box sx={{ display: 'flex' }}>
      <Sidebar />
      <Box
        component="main"
        sx={{
          flexGrow: 1,
          p: 3,
          ml: '240px',
          bgcolor: '#f5f5f5',
          minHeight: '100vh',
        }}
      >
        <Container maxWidth="lg">
          <Box sx={{ mb: 4, display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
            <Typography variant="h4" sx={{ fontWeight: 600, color: '#1a2f4d' }}>
              Danh sách sinh viên
            </Typography>
            <Button
              variant="contained"
              startIcon={<AddIcon />}
              onClick={() => handleOpenDialog()}
              sx={{ bgcolor: '#2196F3', '&:hover': { bgcolor: '#1976D2' } }}
            >
              Thêm sinh viên
            </Button>
          </Box>

          <Paper sx={{ p: 2, mb: 3 }}>
            <TextField
              fullWidth
              variant="outlined"
              placeholder="Tìm kiếm sinh viên..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              InputProps={{
                startAdornment: (
                  <InputAdornment position="start">
                    <SearchIcon />
                  </InputAdornment>
                ),
              }}
              sx={{ mb: 2 }}
            />

            <TableContainer>
              <Table>
                <TableHead>
                  <TableRow>
                    <TableCell>Mã SV</TableCell>
                    <TableCell>Họ và tên</TableCell>
                    <TableCell>Lớp</TableCell>
                    <TableCell>Giới tính</TableCell>
                    <TableCell>Email</TableCell>
                    <TableCell>Ngày sinh</TableCell>
                    <TableCell>Số điện thoại</TableCell>
                    <TableCell align="center">Thao tác</TableCell>
                  </TableRow>
                </TableHead>
                <TableBody>
                  {filteredStudents.map((student) => (
                    <TableRow key={student.MaSV}>
                      <TableCell>{student.MaSV}</TableCell>
                      <TableCell>{student.HoTen}</TableCell>
                      <TableCell>{student.MaLop}</TableCell>
                      <TableCell>{student.GioiTinh}</TableCell>
                      <TableCell>{student.Email}</TableCell>
                      <TableCell>{convertToVietnamDate(student.NgaySinh)}</TableCell>
                      <TableCell>{student.SoDT}</TableCell>
                      <TableCell align="center">
                        <IconButton color="primary" onClick={() => handleOpenDialog(student)}>
                          <EditIcon />
                        </IconButton>
                        <IconButton color="error" onClick={() => handleDelete(student.MaSV)}>
                          <DeleteIcon />
                        </IconButton>
                      </TableCell>
                    </TableRow>
                  ))}
                </TableBody>
              </Table>
            </TableContainer>
          </Paper>
        </Container>

        <Dialog open={openDialog} onClose={handleCloseDialog} maxWidth="sm" fullWidth>
          <DialogTitle>{selectedStudent ? 'Chỉnh sửa sinh viên' : 'Thêm sinh viên mới'}</DialogTitle>
          <DialogContent>
            <Box sx={{ mt: 2 }}>
              <TextField
                fullWidth
                label="Mã sinh viên"
                value={formData.id}
                onChange={(e) => setFormData({ ...formData, id: e.target.value })}
                margin="normal"
                required
                disabled={selectedStudent}
              />
              <TextField
                fullWidth
                label="Họ và tên"
                value={formData.name}
                onChange={(e) => setFormData({ ...formData, name: e.target.value })}
                margin="normal"
                required
              />
              <FormControl fullWidth margin="normal" required>
                <InputLabel>Lớp</InputLabel>
                <Select
                  value={formData.class}
                  onChange={(e) => setFormData({ ...formData, class: e.target.value })}
                  label="Lớp"
                >
                  {lops.map((lop) => (
                    <MenuItem key={lop.MaLop} value={lop.MaLop}>
                      {lop.TenLop}
                    </MenuItem>
                  ))}
                </Select>
              </FormControl>
              <FormControl fullWidth margin="normal" required>
                <InputLabel>Giới tính</InputLabel>
                <Select
                  value={formData.gender}
                  onChange={(e) => setFormData({ ...formData, gender: e.target.value })}
                  label="Giới tính"
                >
                  <MenuItem value="Nam">Nam</MenuItem>
                  <MenuItem value="Nữ">Nữ</MenuItem>
                </Select>
              </FormControl>
              <TextField
                fullWidth
                label="Email"
                value={formData.email}
                onChange={(e) => setFormData({ ...formData, email: e.target.value })}
                margin="normal"
              />
              <TextField
                fullWidth
                label="Số điện thoại"
                value={formData.phone}
                onChange={(e) => setFormData({ ...formData, phone: e.target.value })}
                margin="normal"
              />
            </Box>
          </DialogContent>
          <DialogActions>
            <Button onClick={handleCloseDialog}>Hủy</Button>
            <Button variant="contained" onClick={handleSubmit} color="primary">
              Lưu
            </Button>
          </DialogActions>
        </Dialog>
      </Box>
    </Box>
  );
};

export default Students;
